import React, { Component } from "react";
import NewsItem from "./NewsItems";
// import Loading from "./Loading";
import PropTypes from 'prop-types';
import {InfiniteScroll} from "react-infinite-scroll-component";
export class News extends Component {
static defaultProps={
category:"general",
pageSize:8,
country:"in"
}
static propTypes={
  country:PropTypes.string,
  pageSize:PropTypes.number,
  category:PropTypes.string,
}
capitalize=(string)=>{
 return string.charAt(0).toUpperCase()+string.slice(1);
}
                      constructor(props) {
                        super(props);

                        this.state = {
                          articles: [],
                          loading: false,
                          pages: 1,
                          totalResults:0,
                        };
                      document.title=`${this.capitalize(this.props.category)} - NewsWala`;
                      }
                      // async updatenews(){
                      //   const url =
                      //   `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=edf66adc50674684b9d37df0467f0b3c&page=${this.state.pages}&pageSize=${this.props.pageSize}`;
                      //   this.setState({loading:true})
                      // let data = await fetch(url);
                      // let parsedData = await data.json();
                      // console.log(parsedData);
                      // this.setState({
                      //   articles: parsedData.articles,
                      //   totalResults: parsedData.totalResults,
                      //   loading:false,
                      // });
                      // }
                      async componentDidMount() {
                        let url =
                          `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=edf66adc50674684b9d37df0467f0b3c&page=1&pageSize=${this.props.pageSize}`;
                          this.setState({loading:true})
                        let data = await fetch(url);
                        let parsedData = await data.json();
                        console.log(parsedData);
                        this.setState({
                          articles: parsedData.articles,
                          totalResults: parsedData.totalResults,
                          loading:false,
                        });
                        // this.updatenews();
                      }

                      handlePrev = async () => {
                        let url = `https://newsapi.org/v2/top-headlines?country=in&category=${this.props.category}&apikey=edf66adc50674684b9d37df0467f0b3c&page=${
                          this.state.pages - 1
                        }&pageSize=${this.props.pageSize}`;
                        this.setState({loading:true})
                        let data = await fetch(url);
                        let parsedData = await data.json();
                        console.log(parsedData);

                        this.setState({
                          pages: this.state.pages - 1,
                          articles: parsedData.articles,
                          loading:false,
                        });
                      //   this.setState({pages:this.state.pages-1});
                      // this.updatenews();

                      };
                      handleNext = async () => {
                        if (!(this.state.pages + 1 > Math.ceil(this.state.totalResults /this.props.pagesize)))                       
                      {
                        
                    
                          let url = `https://newsapi.org/v2/top-headlines?country=in&category=${this.props.category}&apikey=edf66adc50674684b9d37df0467f0b3c&page=${
                            this.state.pages + 1
                          }&pageSize=${this.props.pageSize}`;
                          this.setState({loading:true})
                          let data = await fetch(url);

                          let parsedData = await data.json();
                          console.log(parsedData);

                          this.setState({
                            pages: this.state.pages + 1,
                            articles: parsedData.articles,
                            loading:false,
                          });
                      }
                      // this.setState({pages:this.state.pages+1});
                      // this.updatenews();
                      }
  render() {
    return (
      <div className="container my-3 ">
        <h1 className="my-3 display-4 text-center"><u>NewsWala - Top ${this.capitalize(this.props.category)} Headlines</u></h1>
        {/* {this.state.loading && <Loading />} */}
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalResults}
          loader={Loading}
        >
          
        <div className="row ">
          { this.state.articles.map((element) => {
            return (
              <div className="col-md-3 my-3" key={element.url}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 66) : ""}
                  description={element.description ? element.description.slice(0, 111) : "" }
                  imageURL={element.urlToImage}
                  newsurl={element.url}
                  author={element.author}
                  publishedAt={element.publishedAt} source={element.source.name}
                  />
              </div>
            );
          })}
        </div>
          </InfiniteScroll>
        {/* <div className="container d-flex justify-content-between">
          <button disabled={this.state.pages <= 1} type="button" className=" btn  btn-dark bg-dark" onClick={this.handlePrev}>&larr;Previous</button>
          <button disabled={this.state.pages + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)} type="button" className=" btn  btn-dark bg-dark" onClick={this.handleNext}>Next&rarr;</button>
        </div> */}
      </div>
    );
  }
}

export default News;
