import React, { Component } from "react";
import NewsItem from "./NewsItems";
export class News extends Component {

  constructor()
  {
    
    super();


    this.state={
      articles:[],
      loading:false,
      pages:1,
     
    }
  }
 async  componentDidMount()
{
  let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3c&page=1&pageSize:10";
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);
  this.setState({articles:parsedData.articles,totalResults:parsedData.totalResults});

}  

handlePrev=async ()=>{
  let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3c&page=${this.state.pages -1}&pageSize=10`
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);

  this.setState({
    pages:this.state.pages - 1,
    articles:parsedData.articles
  })
}
handleNext=async ()=>{
  if(this.state.pages +1 > Math.ceil (this.state.totalResults/10)){

  }
  else{
  let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3c&page=${this.state.pages +1}&pageSize=10`
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);

  this.setState({
    pages:this.state.pages + 1,
    articles:parsedData.articles
  })
}
}
render() {
  
  
    return (
      <div className="container my-3 ">
        <h1 className="my-3 display-4">NewsWala - Top Headlines</h1>
        <div className="row ">
        {this.state.articles.map((element)=>{
          return  <div className="col-md-3 my-3" key={element.url}>
          <NewsItem title={element.title?element.title.slice(0,66):""} description={element.description?element.description.slice(0,111):""} imageURL={element.urlToImage} newsurl={element.url}/>         
        </div>
        })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.pages<=1} type="button" className=" btn  btn-dark bg-primary" onClick={this.handlePrev}>&larr;Previous</button>
          <button disabled={(this.state.pages+1  > Math.ceil (this.state.totalResults/20))}type="button" className=" btn  btn-dark bg-primary" onClick={this.handleNext}>Next&rarr;</button>
        </div>
      </div>
    );
  }
}

export default News;
