import React, { Component } from "react";
import NewsItem from "./NewsItems";
export class News extends Component {

  constructor()
  {
    
    super();


    this.state={
      articles:[],
      loading:false,
      pages:1,
    }
  }
 async  componentDidMount()
{
  let url=`{https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3c}`;
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);
  this.setState({articles:parsedData.articles});

}  
handlePrev=async ()=>{
  let url=`{https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3cpage=1}`
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);
  this.setState({articles:parsedData.articles});
  this.setState({
    pages:this.state.pages - 1,
    articles:parsedData.articles
  })
}
handleNext=async ()=>{
  let url=`{https://newsapi.org/v2/top-headlines?country=in&apiKey=edf66adc50674684b9d37df0467f0b3c&page=${this.state.pages +1}}`
  let data=await fetch(url);
  let parsedData=await data.json();
  console.log(parsedData);

  this.setState({
    pages:this.state.pages + 1,
    articles:parsedData.articles
  })
}
render() {
  
  
    return (
      <div className="container my-3 ">
        <h1 className="my-3 display-4">NewsWala - Top Headlines</h1>
        <div className="row ">
        {this.state.articles.map((element)=>{
          return  <div className="col-md-3 my-3" key={element.url}>
          <NewsItem title={element.title?element.title.slice(0,66):""} description={element.description?element.description.slice(0,111):""} imageURL={element.urlToImage} newsurl={element.url}/>         
        </div>
        })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disable={this.state.pages<=1} type="button" className=" btn  btn-dark bg-primary" onClick={handlePrev}>&larr;Previous</button>
          <button type="button" className=" btn  btn-dark bg-primary" onClick={handleNext}>Next&rarr;</button>
        </div>
      </div>
    );
  }
}

export default News;
