import React, { Component } from "react";
import './App.css';

export class NewsItem extends Component {

  render() {
    let { title, description,imageURL,newsurl } = this.props;
    return (
      
        <div class="card" style={{ width: "18rem" }}>
          <img
            className="card-img-top"
            src={!imageURL?"https://c.ndtvimg.com/2022-02/qcqb4jg8_ukraine-belarus-exercise-650_625x300_21_February_22.jpg":imageURL} alt="imagehhg"
          />
          <div className="card-body" >
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}...</p>
            <a href={newsurl} rel="noreferrer" target="_blank" className="btn btn-sm btn-dark">
             Read more
            </a>
          </div>
        </div>
    
    );
  }
}

export default NewsItem;
