const News=(props)=>{
  
 const capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };
const [articles, setarticles] = useState([]);
const [totalResults, settotalResults] = useState(0);
const [page, setpage] = useState(1);
const [loading, setloading] = useState(false);

  
const updatenews=async()=>{
    const url =
    `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=6ea1873d933d479f99b97377422a5ccc&page=${this.state.page}&pageSize=${props.pageSize}`;
    setloading(true)
  let data = await fetch(url);
  let parsedData = await data.json();
  console.log(parsedData);

  
    setarticles(parsedData.articles)
    settotalResults(parsedData.totalResults)
    setloading(false)
   
}
  
 useEffect(()=>{
    // let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=6ea1873d933d479f99b97377422a5ccc&page=1&pageSize=${props.pageSize}`;
    // this.setState({ loading: true });
