  //       loading: false,
  //     });
  //   }
    // this.setState({page:this.state.page+1});
    // this.updatenews();
  // };
  const fetchMoreData = async () => {
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=6ea1873d933d479f99b97377422a5ccc&page=${this.state.page+1}&pageSize=${props.pageSize}`;
    setpage(page + 1 );
    setloading(true );
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);

    
      setarticles(articles.concat(parsedData.articles));
      settotalResults(parsedData.totalResults);
      setloading(false);

  }
  
    return (
      <>
        <h1 className="my-3 display-4 text-center">
          <u>
            NewsWala - Top {this.capitalize(props.category)} Headlines
          </u>
        </h1>
        {this.state.loading && <Loading />}
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          // loader={<Loading />}
        >
          <div className="container">
            <div className="row ">
              {this.state.articles.map((element) => {
                return (
                  <div className="col-md-3 my-3" key={element.url}>
                    <Newsitems
                      title={element.title ? element.title.slice(0, 66) : ""}
                      description={
                        element.description
                          ? element.description.slice(0, 111)
                          : ""
                      }
                      imageURL={element.urlToImage}
                      newsurl={element.url}
                      author={element.author}
                      publishedAt={element.publishedAt}
                      source={element.source.name}
                    />
                  </div>
                );
              })}
            </div>
          </div>
        </InfiniteScroll>
        {/* <div className="container d-flex justify-content-between">
