  fetchMoreData = async () => {
    this.setState({ pages: this.state.pages + 1 });
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}
    &apikey=6ea1873d933d479f99b97377422a5ccc&page=${this.state.pages}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let data = await fetch(url);
    let parsedData = await data.json(); 
    console.log(parsedData);
    this.setState({
      articles: this.state.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,
    });
  };
  render() {
    return (
      <div className="container my-3 ">
        <h1 className="my-3 display-4 text-center">
          <u>
            NewsWala - Top {this.capitalize(this.props.category)} Headlines
          </u>
        </h1>
        {this.state.loading && <Loading />}
        <InfiniteScroll dataLength={this.state.articles.length} next={this.fetchMoreData} hasMore={this.state.articles.length !== this.state.totalResults} 
