import React, { Component } from "react";
import './App.css';

export class NewsItem extends Component {

  render() {
    let { title, description,imageURL,newsurl,author,publishedAt,source } = this.props;
    return (
      
        <div className="card" >
          <span class="position-absolute top-0  translate-middle badge rounded-pill bg-danger text-danger" style={{zindex:'1',left:'80%'}}>
 {source}
  </span>
          <img
            className="card-img-top"
            src={!imageURL?"https://c.ndtvimg.com/2022-02/qcqb4jg8_ukraine-belarus-exercise-650_625x300_21_February_22.jpg":imageURL} alt="imagehhg"
          />
          <div className="card-body" >
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}...</p>
            <p class="card-text"><small class="text-muted">By {author} on  {new Date(publishedAt).toGMTString()}</small></p>
            <a href={newsurl} rel="noreferrer" target="_blank" className="btn btn-sm btn-dark">
             Read more
            </a>
          </div>
        </div>
    
    );
  }
}

export default NewsItem;
