import React, { Component } from "react";
import Navbar from "./Components/Navbar";
import News from "./Components/News";
import LoadingBar from 'react-top-loading-bar';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";

class App extends Component {
  pageSize=12;
  state={progress:0}
  setProgress=(progress)=>{
    this.setState({progress:progress})
  }
  render() {
    return (
      <>
        <div>
          <Router>
          <Navbar />
          <LoadingBar 
          color="red" progress={this.state.progress}
          />


            <Routes>
          <Route exact path="/" element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="general" key="general" country="in" />}></Route>
          <Route exact path="/business"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="business" key="business" country="in" />}></Route>
          <Route exact path="/entertainment"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="entertainment" key="entertainment" country="in" />}></Route>
          <Route exact path="/general" key="general" element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} key="general" category="general" country="in" />}></Route>
          <Route exact path="/health"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="health" key="health" country="in" />}></Route>
          <Route exact path="/science"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="science" key="science" country="in" />}></Route>
          <Route exact path="/sports"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} category="sports" key="sports" country="in" />}></Route>
          <Route exact path="/technology"  element={<News setProgress={this.setProgress}setProgress={setProgress} pageSize={this.pageSize} key="technology" category="technology" country="in" />}></Route>
          </Routes>
          </Router>
        </div>

      </>
    );
  }
}

export default App;
